import React from 'react';
import classes from './MyPosts.module.scss';
import Post from './Post/Post.jsx'

const MyPosts = (props) => {
let newPostElement = React.createRef();
  let addPost = () => {
    alert(newPostElement);
  }

  let postElements =
    props.postsData.map(post => (<Post name={post.name} message={post.message} likes={post.likes} dislikes={post.dislikes} />));

  return (
    <div className={classes.content}>
      <textarea ref = {newPostElement} className={classes.textArea} cols="80" rows="3"></textarea>
      <div className={classes.postButtons}>
        <button onClick={addPost} className={classes.addPost + ' ' + classes.btn}>Add New</button>
        <button className={classes.removePost + ' ' + classes.btn}>Remove</button>
      </div>
      <hr />
      {postElements}
    </div>
  );
}
export default MyPosts;
