import './App.scss';
import Header from './components/Header/Header';
import Navbar from './components/Navbar/Navbar';
import Profile from './components/Profile/Profile';
import Messages from './components/Messages/Messages';
import News from './components/News/News';
import Music from './components/Music/Music';
import Photos from './components/MyPhotos/Photos';
import ToDoList from './components/ToDoList/ToDoList';
import Setting from './components/Setting/Setting';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Context from './context';
import React from 'react';

const App = (props) => {
  const [todos, setTodos] = React.useState([
    { id: 1, completed: false, title: "Buy this" },
    { id: 2, completed: false, title: "Buy second" },
    { id: 3, completed: false, title: "Buy some" }
  ])
  function toggleToDo(id) {
    setTodos(
      todos.map(todo => {
        if (todo.id === id) {   // Если id массива совпадает с id выбранного элемента меняем False на True
          todo.completed = !todo.completed   // False to True to False
        }
        return todo // возвращаем измененный массив
      })
    )
  }
  function removeToDo(id) {
    setTodos(todos.filter(todo => todo.id !== id))
  }
  return (
      <BrowserRouter >
        <div className='app-wrapper-grid'>
          <Header name='Anton' age='99' />
          <Navbar />
          <div className='app-wrapper-content'>
            <Routes>
              <Route path="/profile" element={<Profile
                postsData={props.state.profilePage.postsData}
                updateTextArea={props.updateTextArea}
                newPostText={props.state.profilePage.newPostText}
                addPosts={props.addPosts}
              />}
              />
              <Route exact path="/messages/*" element={<Messages dialogsData={props.state.messagesPage.dialogsData} messagesData={props.state.messagesPage.messagesData} />} />
              <Route path="/news" element={<News />} />
              <Route path="/music" element={<Music />} />
              <Route path="/photos" element={<Photos />} />
              <Context.Provider value={{removeToDo}}> {/*Чтобы передать функцию по удалению элемента оборачиваем все в Context.Provider*/}
              <Route path="/todolist" element={<ToDoList todos={todos} onToggle={toggleToDo} />} />
              </Context.Provider>
              <Route path="/setting" element={<Setting />} />
            </Routes>
          </div>
        </div>
      </BrowserRouter>


  );
}

export default App
