import React from 'react';
import classes from './MyPosts.module.css';
import Post from './Post/Post.jsx'

const MyPosts = () => {
  const postsData = [
    {name: 'Anton', message: 'HI, im new', likes: 10, dislikes: 1},
    {name: 'Andrew', message: 'I learn REact', likes: 130, dislikes: 1},
    {name: 'Jack', message: 'Yo', likes: 120, dislikes: 3},
    {name: 'John', message: 'HELLO', likes: 13, dislikes: 4},
    {name: 'kai', message: 'I like programming', likes: 410, dislikes: 2}
  ];
    return (
      <div className={classes.content}>
          <textarea name="" id="" cols="80" rows="3"></textarea>
          <div className={classes.postButtons}>
          <button>Add New</button>
          <button>Remove</button>
          </div>
          postsData.map(() => ({}))

          <Post name={postsData[0].name} message={postsData[0].message} likes={postsData[0].likes} dislikes={postsData[0].dislikes} />
          <Post name={postsData[1].name} message={postsData[1].message} likes={postsData[1].likes} dislikes={postsData[1].dislikes} />
          <Post name={postsData[2].name} message={postsData[2].message} likes={postsData[2].likes} dislikes={postsData[2].dislikes} />
          <Post name={postsData[3].name} message={postsData[3].message} likes={postsData[3].likes} dislikes={postsData[3].dislikes} />
          <Post name={postsData[4].name} message={postsData[4].message} likes={postsData[4].likes} dislikes={postsData[4].dislikes} />
      </div>

    );
}
export default MyPosts;
