import React from 'react'
import classes from './Messages.module.scss'
import {NavLink} from 'react-router-dom'

const Messages = () => {
    const dialogsData = [
        {id: '1', name: 'Anton'},
        {id: '2', name: 'Andrey'},
        {id: '3', name: 'Anya'},
        {id: '4', name: 'Alena'},
        {id: '5', name: 'Anastasia'}
    ]
    const messagesData = [
        {id: '1', message: 'Hello'},
        {id: '2', message: 'Sup Bro'},
        {id: '3', message: 'Hey hey hey'},
        {id: '4', message: 'React is nice'}
    ]

    let dialogElements = dialogsData.map(dialog => <DialogItem id={dialog.id} name={dialog.name}/>)
    let messageElements = messagesData.map(message => <MessageItem id={message.id} message={message.message}/>)
    
    return (
        <div className={classes.dialogs}>
        <div className={classes.dialogItems}>
            { dialogElements }
        </div>
        <div className={classes.messageItem}>
            { messageElements }
        </div>
        </div>
    );
}

const DialogItem = (props) => {
    let path = '/messages/' + props.id;
    return (
        <div className={classes.dialogItems + classes.dialog}>
            <NavLink to={path} className={(navData) => navData.isActive ? classes.activeLink : classes.dialogLink}>{props.name}</NavLink>
        </div>
    );
}

const MessageItem = (props) => {
    return (
        <div className={classes.messages}>
          {props.message}
        </div>
    );
}

export default Messages
