import React from 'react'
import classes from './Messages.module.css'
import {NavLink} from 'react-router-dom'

const Messages = () => {
    return (
        <div>
            <div className={classes.dialogItems}>
            <DialogItem id='1' name='Anton' />
            <DialogItem id='2' name='Andrey' />
            <DialogItem id='3' name='Anya' />
            <DialogItem id='4' name='Alena' />
            <DialogItem id='5' name='Anastasia' />
        </div>
            <div className={classes.messages}>
            <Message message="Hello" />
            <Message message="Yappi" />
            <Message message="YO" />
            <Message message="Hey" />
            </div>
        </div>

    );
}

const DialogItem = (props) => {
    let pathToDialog = '/messages/' + props.id;
    return (
        <div className={classes.dialogItems}>
            <NavLink to={pathToDialog} className={(navData) => navData.isActive ? classes.activeLink : classes.dialogLink}>{props.name}</NavLink>
        </div>
    );
}

const Message = (props) => {
    return (
        <div className={classes.message}>
            <NavLink to={pathToDialog} className={message}>{props.message}</NavLink>
        </div>
    );
}


export default Messages
