      { name: 'An', message: 'react-redux there is learning', likes: 4130, dislikes: 2 }
    ],
  },
    messagesPage: {
    dialogsData: [
      { id: '1', name: 'Anton' },
      { id: '2', name: 'Andrey' },
      { id: '3', name: 'Anya' },
      { id: '4', name: 'Alena' },
      { id: '5', name: 'Anastasia' },
      { id: '6', name: 'Anastasia' },
      { id: '7', name: 'Anastasia' },
      { id: '8', name: 'Anastasia' }
    ],
    messagesData: [
      { id: '1', message: 'Hello' },
      { id: '2', message: 'Sup Bro' },
      { id: '3', message: 'Hey hey hey' },
      { id: '4', message: 'Hey hey hey' },
      { id: '5', message: 'Hey hey hey' },
      { id: '6', message: 'Hey hey hey' },
      { id: '7', message: 'Hey hey hey' },
      { id: '8', message: 'React is nice' }
    ]
  },
  users: ['Anton', 'Andrey'] 
}

export let addPosts = (postMessage) => {
  debugger;
  let newPost = {
    id:5,
    message: postMessage,
    likes: 0
  }
  state.profilePage.postsData.push(newPost);
  render(state)
}

export default state;
