import React from 'react';
import classes from './MyPosts.module.css';
import Post from './Post/Post.jsx'

const MyPosts = () => {
  const postsData = [
    {name: 'Anton', message: 'HI, im new', likes: 10, dislikes: 1},
    {name: 'Andrew', message: 'I learn REact', likes: 130, dislikes: 1},
    {name: 'Jack', message: 'Yo', likes: 120, dislikes: 1},
    {name: 'John', message: 'HELLO', likes: 13, dislikes: 4},
    {name: 'kai', message: 'I like programming', likes: 410, dislikes: 2}
  ];
    return (
      <div className={classes.content}>
          <textarea name="" id="" cols="80" rows="3"></textarea>
          <div className={classes.postButtons}>
          <button>Add New</button>
          <button>Remove</button>
          </div>
          <Post name={postsData[0].name} message={postsData[0].message} likes={postsData[0].likes} dislikes={postsData[0].dislikes} />
          <Post name="Andrew" message="HELLO" likes="40" dislikes="2" />
          <Post name="Jack" message="I learn REact" likes="130" dislikes="3"/>
          <Post name="John" message="Yo" likes="110" dislikes="4"/>
          <Post name="Kai" message="I like programming" likes="999" dislikes="1"/>
      </div>

    );
}
export default MyPosts;
