import React from 'react'
import classes from './Messages.module.scss'
import {NavLink} from 'react-router-dom'

const Messages = () => {
    const dialogsData = [
        {id: '1', name: 'Anton'},
        {id: '2', name: 'Anton'},
        {id: '3', name: 'Anton'},
        {id: '4', name: 'Anton'},
        {id: '5', name: 'Anton'},
        {id: '6', name: 'Anton'}
    ]
    return (
        <div className={classes.dialogs}>
        <div className={classes.dialogItems}>
            <DialogItem id='1' name='Anton' />
            <DialogItem id='2' name='Andrey' />
            <DialogItem id='3' name='Anya' />
            <DialogItem id='4' name='Alena' />
            <DialogItem id='5' name='Anastasia' />
        </div>
        <div className={classes.messages}>
            <Message message="Hello" />
            <Message message="Yappi" />
            <Message message="YO" />
            <Message message="Hey" />
            </div>
        </div>
    );
}

const DialogItem = (props) => {
    let path = '/messages/' + props.id;
    return (
        <div className={classes.dialogItems + classes.dialog}>
            <NavLink to={path} className={(navData) => navData.isActive ? classes.activeLink : classes.dialogLink}>{props.name}</NavLink>
        </div>
    );
}

const Message = (props) => {
    return (
        <div className={classes.dialog}>
          {props.message}
        </div>
    );
}

export default Messages
