import React from 'react'
import classes from './Messages.module.scss'
import {NavLink} from 'react-router-dom'

const Messages = () => {
    const dialogsData = [
        {id: '1', name: 'Anton'},
        {id: '2', name: 'Andrey'},
        {id: '3', name: 'Anya'},
        {id: '4', name: 'Alena'},
        {id: '5', name: 'Anastasia'}
    ]
    const messagesData = [
        {id: '1', message: 'Hello'},
        {id: '2', message: 'Sup Bro'},
        {id: '3', message: 'Hey hey hey'},
        {id: '4', message: 'React is nice'}
    ]
    return (
        <div className={classes.dialogs}>
        <div className={classes.dialogItems}>
            <DialogItem id={dialogsData[0].id} name={dialogsData[0].name} />
            <DialogItem id={dialogsData[1].id} name={dialogsData[1].name} />
            <DialogItem id={dialogsData[2].id} name={dialogsData[2].name} />
            <DialogItem id={dialogsData[3].id} name={dialogsData[3].name} />
            <DialogItem id={dialogsData[4].id} name={dialogsData[4].name} />
        </div>
        <div className={classes.messageItem}>
            <Message id={messagesData[0].id} name={messagesData[0].message}/>
            <Message id={messagesData[1].id} name={messagesData[1].message}/>
            <Message id={messagesData[2].id} name={messagesData[2].message}/>
            <Message id={messagesData[3].id} name={messagesData[3].message}/>
            </div>
        </div>
    );
}

const DialogItem = (props) => {
    let path = '/messages/' + props.id;
    return (
        <div className={classes.dialogItems + classes.dialog}>
            <NavLink to={path} className={(navData) => navData.isActive ? classes.activeLink : classes.dialogLink}>{props.name}</NavLink>
        </div>
    );
}

const Message = (props) => {
    let path = '/messages/' + props.id;
    return (
        <div className={classes.dialog}>
          {props.message}
        </div>
    );
}

export default Messages
