import React from 'react'
import classes from './Messages.module.css'
import {NavLink} from 'react-router-dom'

const DialogItem = (props) => {
    let pathToDialog = '/messages/' + props.id;

    return (
        <div className={classes.dialog}>
           <NavLink to={pathToDialog}className={navData => navData.isActive ? classes.activeLink : classes.dialogLink}>{props.name}</NavLink>                     
        </div>       
    );
}

const Messages = (props) => {
    return (
        <div className={classes.dialogs}>
        <div className={classes.dialogItems}>              
                <DialogItem name='Anton' id='1' />
                <DialogItem name='Artem' id='2' />
                <DialogItem name='Andrey' id='3' />
        </div>
        <div className={classes.message}>
            <div className={classes.dialog}>Hello. How are You</div>
            <div className={classes.dialog}>How to learn React</div>
            <div className={classes.dialog}>Yappi</div>
        </div>
        </div>
    );
    
}


export default Messages
