import React from 'react'
import classes from './Messages.module.css'
import {NavLink} from 'react-router-dom'

const dialogItem = (props) => {
    let path = '/messages/' + props.id;

    return (
        <div className={classes.dialog}>
           <NavLink to={path}> className={navData => navData.isActive ? classes.activeLink : classes.dialogLink}>Anton</NavLink>         
                    
                </div>
        
    );
}



const Messages = (props) => {
    return (
        <div className={classes.dialogs}>
        <div className={classes.dialogItems}>
                
                <Dialogitem name='Anton' id='1' />
                <div className={classes.dialog}>
                    <NavLink to='/messages/2' className={navData => navData.isActive ? classes.activeLink : classes.dialogLink}>Artem</NavLink>
                </div>
                <div className={classes.dialog}>
                    <NavLink to='/messages/3' className={navData => navData.isActive ? classes.activeLink : classes.dialogLink}>Andrey</NavLink>
                </div>
        </div>
        <div className={classes.message}>
            <div className={classes.dialog}>Hello. How are You</div>
            <div className={classes.dialog}>How to learn React</div>
            <div className={classes.dialog}>Yappi</div>
        </div>
        </div>
    );
}

export default Messages
