import React from 'react';
import classes from './ToDoList.module.scss';
import ToDoItem from './ToDoItem';
import PropTypes from 'prop-types';

const ToDoList = (props) => {
    const styles = {    // Стили для ToList
        toDoWrapper: {
            width: '20rem',
        },
        ul: {
            listStyle: 'none',
            margin: '.5rem',
            padding: 0,
            lineHeight: '2em',
            color: 'white',
            fontSize: '1em',
            fontWeight: 600,
            position: 'relative',
        },
        h2: {
            fontSize: '1.5em',
            color: 'aqua',
            position: 'relative',
            textDecoration: 'underline',
            margin: '1rem'
        }
    }
    return (
        <div className={classes.toDoWrapper} style={styles.toDoWrapper}>
            <h2 style={styles.h2}>ToDo List</h2>
            <ul style={styles.ul}>
                {props.todos.map((todo, i) => <ToDoItem todo={todo} key={todo.id} index={i} onChange={props.onToggle} />)}   {/* Мапим через props наш миссив todos из App на ToDoItem*/}
            </ul>
        </div>
    )
}

ToDoList.propTypes = {
    todos: PropTypes.arrayOf(PropTypes.object).isRequired,
    onToggle: PropTypes.func.isRequired
}


export default ToDoList;
