import React from 'react';
import classes from './ToDoList.module.scss';
import ToDoItem from './ToDoItem';


const ToDoList = (props) => {
    const styles = {    // Стили для ToList
        ul: {
            listStyle: 'none',
            margin: '10px',
            padding: 0,
            lineHeight: '2em',
            color: 'white',
            fontSize: '1.5em',
            fontWeight: 600         

        },
        h2: {
            fontSize: '34px',
            color: 'aqua',
            textAlign: 'center',
            textDecoration: 'underline'
        }
    }
    return (
        <div className={classes.toDoWrapper}>
            <h2 style={styles.h2}>ToDo List</h2>
            <ul style={styles.ul}>
                {props.todos.map((todo, i) => <ToDoItem todo={todo} key={todo.id} index={i}/>)}   {/* Мапим через props наш миссив todos из App на ToDoItem*/}
            </ul>
        </div>
    )

}



export default ToDoList;
